// ========== Parameters ==========
// Unique user identifier for the incident (set the UPN of the user you're investigating)
let IncidentUserPrincipalName =  '@{concat(item()?['Name'],'@',item()?['UPNSuffix'])}';
// Optional: Object ID of the user (can be used for joins or additional filtering if needed)
let IncidentUserID = '@{item()?['AadUserId']}';
// How far back to look for *recent* successful sign-ins (e.g. last 7 days)
let RecentLoginsTimeWindow = 7d;
// How far back to look for *past* successful sign-ins and compare them to *recent* activity (e.g. last 90 days, excluding recent period)
let PastLoginsTimeWindow = 90d;
let RecentLoginsRaw = 
    SigninLogs
    | where TimeGenerated >= ago(RecentLoginsTimeWindow)
    | where ResultType == 0
    | where UserPrincipalName =~ IncidentUserPrincipalName or UserId == IncidentUserID
    | extend DeviceTrustType = tostring(DeviceDetail.trustType)
    | extend DeviceRegistered = iff(isnotempty(DeviceTrustType) and DeviceTrustType != "Unregistered", 1, 0)
    | extend geoInfo = geo_info_from_ip_address(IPAddress)
    | extend Country = tostring(geoInfo.country)
    | extend City = tostring(geoInfo.city)
    | project IPAddress, UserPrincipalName, DeviceRegistered, Country, City;
let PastLoginsRaw = 
    union isfuzzy=true 
        (
            SigninLogs
            | extend ErrorCode = tostring(ResultType)
            | project IPAddress, UserPrincipalName, UserId, TimeGenerated, ErrorCode
        ),
        (
            AADNonInteractiveUserSignInLogs
            | extend ErrorCode = tostring(ResultType)
            | project IPAddress, UserPrincipalName, UserId, TimeGenerated, ErrorCode
        )
    | where TimeGenerated between (ago(PastLoginsTimeWindow) .. ago(RecentLoginsTimeWindow))
    | where UserPrincipalName =~ IncidentUserPrincipalName or UserId == IncidentUserID
    | where ErrorCode == '0';
let PastIPs = 
    PastLoginsRaw
    | distinct IPAddress;
let PastGeoInfo = 
    PastLoginsRaw
    | extend geoInfo = geo_info_from_ip_address(IPAddress)
    | extend PastCountry = tostring(geoInfo.country), PastCity = tostring(geoInfo.city)
    | summarize PastCountries = make_set(PastCountry), PastCities = make_set(PastCity) by UserPrincipalName;
RecentLoginsRaw
| summarize arg_max(DeviceRegistered, Country, City) by IPAddress, UserPrincipalName
| extend DeviceRegistered = iff(DeviceRegistered == 1, 'Yes', 'No')
| extend PreviouslyUsedIP = iff(IPAddress in (PastIPs), 'Yes', 'No')
| join kind=leftouter (PastGeoInfo) on UserPrincipalName
| extend 
    Country = coalesce(Country, "-"),
    City = coalesce(City, "-"),
    PastCountries = iif(isnull(PastCountries), dynamic([]), PastCountries),
    PastCities = iif(isnull(PastCities), dynamic([]), PastCities)
| extend 
    PreviouslyUsedCountry = iff(array_index_of(PastCountries, Country) != -1, 'Yes', 'No'),
    PreviouslyUsedCity = iff(array_index_of(PastCities, City) != -1, 'Yes', 'No')
| project IPAddress, UserPrincipalName, DeviceRegistered, Country, City, PreviouslyUsedIP, PreviouslyUsedCountry, PreviouslyUsedCity

// ========== Parameters ==========
// Unique user identifier for the incident (set the UPN of the user you're investigating)
let IncidentUserPrincipalName =  '@{concat(item()?['Name'],'@',item()?['UPNSuffix'])}';
// Optional: Object ID of the user (can be used for joins or additional filtering if needed)
let IncidentUserID = '@{item()?['AadUserId']}';
AuditLogs
| where TimeGenerated >= ago(1d)
| where OperationName has 'User registered security info'
| where Result == 'success'
| extend TargetUser = tostring(parse_json(TargetResources)[0].userPrincipalName)
| where TargetUser =~ IncidentUserPrincipalName 
| extend AuthRegIPAddress = parse_json(tostring(InitiatedBy.user)).ipAddress
| project
    AuthRegTime = TimeGenerated,
    UserId = tolower(TargetUser),
    OperationName,
    AuthRegResult = Result,
    AuthRegIPAddress;

// ========== Parameters ==========
// Unique user identifier for the incident (set the UPN of the user you're investigating)
let IncidentUserPrincipalName =  '@{concat(item()?['Name'],'@',item()?['UPNSuffix'])}';
// Optional: Object ID of the user (can be used for joins or additional filtering if needed)
let IncidentUserID = '@{item()?['AadUserId']}';
AuditLogs
| where TimeGenerated >= ago(1d)
| where OperationName == 'Consent to application'
| extend InitiatedByUserPrincipalName = tostring(todynamic(InitiatedBy) ['user']['userPrincipalName']), UserID = tostring(todynamic(InitiatedBy) ['user']['id'])
| where (IncidentUserPrincipalName != '' and IncidentUserPrincipalName =~ InitiatedByUserPrincipalName) or (IncidentUserID != "" and IncidentUserID =~ UserID)
| extend Target_CloudApplication_Name = tostring(todynamic(TargetResources)[0]['displayName']), Target_CloudApplication_AppId = tostring(todynamic(TargetResources)[0]['id'])
| project TimeGenerated, Result, OperationName, InitiatedByUserPrincipalName, Target_CloudApplication_Name, Target_CloudApplication_AppId
